import com.example.eMarketplace.model.User;
import com.example.eMarketplace.repository.PostRepository;
import io.jsonwebtoken.lang.Assert;
import jakarta.persistence.Column;
import jakarta.persistence.Id;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;
import org.springframework.boot.autoconfigure.data.web.SpringDataWebProperties;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.PageImpl;
import org.springframework.data.domain.PageRequest;
import org.springframework.data.domain.Pageable;
import org.springframework.http.ResponseEntity;

import java.sql.Date;
import java.sql.Timestamp;
import java.time.Instant;
import java.time.LocalDate;
import java.time.LocalDateTime;
import java.time.ZoneOffset;
import java.util.List;
import java.util.Optional;
import java.util.UUID;

import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.ArgumentMatchers.eq;
import static org.mockito.Mockito.*;

@ExtendWith(MockitoExtension.class)
public class PostServiceTest {
    @Mock
    private PostRepository postRepository;
    @InjectMocks
    private PostService postService;

    @Test
    void testGetModel() {
        var id = "224ec0da-728e-454a-ac0e-efef33116eb5";
        var name = "propesionali kata";
        var price = 900;
        var description = "magari kataa damijeret";
        var submissionTime = Timestamp.from(
                LocalDateTime.of(2025, 6, 21, 18, 56, 22, 982_398_000)
                        .toInstant(ZoneOffset.UTC)
        );
        var photoUrl = "data:image/jpeg;base64,";

        var post = Post.builder()
                .id(id)
                .description(description)
                .price(price)
                .submissionTime(submissionTime)
                .photoUrl(photoUrl)
                .build();

        when(postRepository.findById(eq(id))).thenReturn(Optional.of(post));

        var result = postService.getModel(id);

        assertEquals(post, result);

    }

    @Test
    void testFalseGetModel() {
        var id = "invalid id";

        when(postRepository.findById(eq(id))).thenReturn(Optional.empty());

        var exception = org.junit.jupiter.api.Assertions.assertThrows(
                IllegalArgumentException.class,
                () -> postService.getModel(id)
        );

        assertEquals("No Shop found with id: " + id, exception.getMessage());

    }

    @Test
    void testSaveAndDeletePost() {
        var id = UUID.randomUUID().toString();
        var name = "Cute dog";
        var price = 900;
        var description = "Very cute dog";
        var submissionTime = Timestamp.from(Instant.now());
        var photoUrl = "picture of cute dog";

        var post = Post.builder()
                .id(id)
                .description(description)
                .price(price)
                .submissionTime(submissionTime)
                .photoUrl(photoUrl)
                .build();

        when(postRepository.save(any(Post.class))).thenReturn(post);
        when(postRepository.findById(id)).thenReturn(Optional.of(post));

        var savedPost = postService.save(post);
        assertEquals(post, savedPost);

        var fetchedPost = postService.getModel(id);
        assertEquals(post.getId(), fetchedPost.getId());

        doNothing().when(postRepository).deleteById(id);
        postService.delete(id);
        when(postRepository.findById(id)).thenReturn(Optional.empty());
        var ex = assertThrows(IllegalArgumentException.class, () -> postService.getModel(id));
        assertEquals("No Shop found with id: " + id, ex.getMessage());

        verify(postRepository).save(post);
        verify(postRepository, times(2)).findById(id);
        verify(postRepository).deleteById(id);
    }

    @Test
    void testUpdatePostExists() {
        var id = "test-id";
        var post = new Post(); 

        when(postRepository.existsById(id)).thenReturn(true);
        when(postRepository.save(post)).thenReturn(post);

        postService.update(id, post);

        verify(postRepository).save(post);
    }

    @Test
    void testUpdatePostNotExists() {
        var id = "missing-id";
        var post = new Post();

        when(postRepository.existsById(id)).thenReturn(false);

        var ex = assertThrows(IllegalArgumentException.class, () -> postService.update(id, post));
        assertEquals("No Shop found with id: " + id, ex.getMessage());
    }

    @Test
    void testGetAllByTimeDesc() {
        Pageable pageable = PageRequest.of(0, 6);
        Page<Post> mockPage = new PageImpl<>(List.of(new Post())); // mock result

        when(postRepository.findAllByOrderBySubmissionTimeDesc(pageable)).thenReturn(mockPage);

        Page<Post> result = postService.getAllByTimeDesc(pageable);

        assertEquals(1, result.getContent().size());
        verify(postRepository).findAllByOrderBySubmissionTimeDesc(pageable);
    }

    @Test
    void testGetAllByTimeAsc() {
        Pageable pageable = PageRequest.of(0, 6);
        Page<Post> mockPage = new PageImpl<>(List.of());

        when(postRepository.findAllByOrderBySubmissionTimeAsc(pageable)).thenReturn(mockPage);

        Page<Post> result = postService.getAllByTimeAsc(pageable);

        assertTrue(result.getContent().isEmpty());
        verify(postRepository).findAllByOrderBySubmissionTimeAsc(pageable);
    }

    @Test
    void testGetAllByPriceDesc() {
        Pageable pageable = PageRequest.of(0, 6);
        Page<Post> mockPage = new PageImpl<>(List.of(new Post(), new Post()));

        when(postRepository.findAllByOrderByPriceDesc(pageable)).thenReturn(mockPage);

        Page<Post> result = postService.getAllByPriceDesc(pageable);

        assertEquals(2, result.getContent().size());
        verify(postRepository).findAllByOrderByPriceDesc(pageable);
    }

    @Test
    void testGetAllByPriceAsc() {
        Pageable pageable = PageRequest.of(0, 6);
        Page<Post> mockPage = new PageImpl<>(List.of());

        when(postRepository.findAllByOrderByPriceAsc(pageable)).thenReturn(mockPage);

        Page<Post> result = postService.getAllByPriceAsc(pageable);

        assertTrue(result.getContent().isEmpty());
        verify(postRepository).findAllByOrderByPriceAsc(pageable);
    }


}
